package operatorHW1st;

public class Arithmeticoperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1,num2;
		num1=100;
		num2=100;
		int add=num1+num2;          //addition
		int sub=num1-num2;          //substraction
		int mult=num1*num2;         //multiplication
		int div=num1/num2;			//division
	System.out.println("addition of two numbers is:- "+add);
	System.out.println("substraction of two numbers is:- "+sub);
	System.out.println("multiplication of two numbers is:- "+mult);
	System.out.println("division of two numbers is:- "+div);

	}

}
